package com.xja.yygh.common.exception;import com.xja.yygh.common.result.Result;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;/** * @Author xiaoYan * @Data 2022/12/4 16:14 * 时间不早了  注意休息 * 以json方式返回统一异常@ResponseBody */@ControllerAdvicepublic class GlobalExceptionHandler {    @ExceptionHandler(Exception.class)    @ResponseBody    public Result error(Exception e){        e.printStackTrace();        return Result.fail();    }    @ExceptionHandler(YyghException.class)    @ResponseBody    public Result error(YyghException e){        e.printStackTrace();        return Result.fail();    }}