package com.xja.yygh.rabbit.common.config;import org.springframework.amqp.support.converter.Jackson2JsonMessageConverter;import org.springframework.amqp.support.converter.MessageConverter;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @Author xiaoYan * @Data 2022/12/22 16:19 * 时间不早了  注意休息 * 消息转换器 */@Configurationpublic class MQConfig {    @Bean    public MessageConverter messageConverter(){        return new Jackson2JsonMessageConverter();    }}