package com.xja.yygh.rabbit.common.service;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @Author xiaoYan * @Data 2022/12/22 16:18 * 时间不早了  注意休息 */@Servicepublic class RabbitService {    @Autowired    private RabbitTemplate rabbitTemplate;    /**     *  发送消息     * @param exchange 交换机     * @param routingKey 路由键     * @param message 消息     */    public boolean sendMessage(String exchange, String routingKey, Object message) {        rabbitTemplate.convertAndSend(exchange, routingKey, message);        return true;    }}