package com.xja.yygh.hosp.config;import com.baomidou.mybatisplus.extension.plugins.PaginationInterceptor;import org.mybatis.spring.annotation.MapperScan;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * @Author xiaoYan * @Data 2022/12/4 11:41 * 时间不早了  注意休息 */@Configuration@MapperScan("com.xja.yygh.hosp.mapper")public class HospConfig {    /**    * 分页插件    */    @Bean    public PaginationInterceptor paginationInterceptor() {        return new PaginationInterceptor();    }}