package com.xja.yygh.hosp.controller;import com.xja.yygh.common.result.Result;import com.xja.yygh.hosp.service.HospitalService;import com.xja.yygh.model.hosp.Hospital;import com.xja.yygh.vo.hosp.HospitalQueryVo;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.web.bind.annotation.*;import java.util.Map;/** * @Author xiaoYan * @Data 2022/12/14 19:53 * 时间不早了  注意休息 */@RestController@RequestMapping("/admin/hosp/hospital")public class HospitalController {    @Autowired    private HospitalService hospitalService;    /**    * @return    * @author xiaoYan    * @params    * @date 2022/12/14 19:54     * 医院列表    */    @ApiOperation("医院列表")    @GetMapping("list/{page}/{limit}")    public Result getHosptalList(@PathVariable Integer page,                                 @PathVariable Integer limit,                                 HospitalQueryVo hospitalQueryVo){        Page<Hospital> hospPageList = hospitalService.selectHospPage(page,limit,hospitalQueryVo);        return Result.ok(hospPageList);    }    /**     * @return     * @author xiaoYan     * @params     * @date 2022/12/14 19:54     * 医院列表     */    @ApiOperation("更新医院上线状态")    @GetMapping("updateHospStatus/{id}/{status}")    public Result updateStatus(@PathVariable String id, @PathVariable Integer status){        hospitalService.updateStatus(id,status);        return Result.ok();    }    @ApiOperation("医院详情信息")    @GetMapping("showHospDetail/{id}")    public Result showHospDetail (@PathVariable String id){        System.out.println(id);        Map<String,Object> map = hospitalService.getHospById(id);        return Result.ok(map);    }}