package com.xja.yygh.msm.receiver;import com.rabbitmq.client.Channel;import com.xja.yygh.msm.service.MsmService;import com.xja.yygh.rabbit.common.constant.MqConst;import com.xja.yygh.vo.msm.MsmVo;import org.springframework.amqp.core.Message;import org.springframework.amqp.rabbit.annotation.Exchange;import org.springframework.amqp.rabbit.annotation.Queue;import org.springframework.amqp.rabbit.annotation.QueueBinding;import org.springframework.amqp.rabbit.annotation.RabbitListener;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;/** * @Author xiaoYan * @Data 2022/12/22 16:27 * 时间不早了  注意休息 */@Componentpublic class MsmReceiver {    @Autowired    private MsmService msmService;    @RabbitListener(bindings = @QueueBinding(            value = @Queue(value = MqConst.QUEUE_MSM_ITEM, durable = "true"),            exchange = @Exchange(value = MqConst.EXCHANGE_DIRECT_MSM),            key = {MqConst.ROUTING_MSM_ITEM}    ))    public void send(MsmVo msmVo, Message message, Channel channel) {        msmService.send(msmVo);    }}