package com.xja.yygh.order;/** * @Author xiaoYan * @Data 2022/12/22 15:02 * 时间不早了  注意休息 */import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.cloud.openfeign.EnableFeignClients;import org.springframework.context.annotation.ComponentScan;@SpringBootApplication@ComponentScan(basePackages = "com.xja")@EnableDiscoveryClient@EnableFeignClients(basePackages = {"com.xja"})public class ServiceOrderApplication {    public static void main(String[] args) {        SpringApplication.run(ServiceOrderApplication.class, args);    }}