package com.xja.yygh.oss;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.autoconfigure.jdbc.DataSourceAutoConfiguration;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.context.annotation.ComponentScan;/** * @Author xiaoYan * @Data 2022/12/21 16:10 * 时间不早了  注意休息 *///取消数据源自动配置@SpringBootApplication(exclude = DataSourceAutoConfiguration.class)@EnableDiscoveryClient@ComponentScan(basePackages = {"com.xja"})public class ServiceOssApplication {    public static void main(String[] args) {        SpringApplication.run(ServiceOssApplication.class,args);    }}