package com.xja.yygh.user;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.cloud.openfeign.EnableFeignClients;import org.springframework.context.annotation.ComponentScan;/** * @Author xiaoYan * @Data 2022/12/4 11:12 * 时间不早了  注意休息 */@ComponentScan(basePackages = {"com.xja"})@SpringBootApplication@EnableDiscoveryClient@EnableFeignClients(basePackages = "com.xja")public class ServiceUserApplication {    public static void main(String[] args) {        SpringApplication.run(ServiceUserApplication.class, args);    }}